package org.tnsif.intro;

public class DatatypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte type
		byte a=127;
		
		//integer byte
		int b=128;
		long b1=826594648l;
		
		//float
		float c=12/5;
		float d=5/2.5f;
		
		//double
		double e=12.5;
		double f=12d/5d;
		
		//character
		char z='a';
		
		//ASCII
		int x='c';
		
		//string 
		String name="Sahil";
		
		//boolean type
		boolean y=true;
			
		System.out.println("The value of a is:" +a);
		System.out.println("The value of b is:" +b);
		System.out.println("The value of c is:" +c);
		System.out.println("The value of d is:" +d);
		System.out.println("The value of e is:" +e);
		System.out.println("The value of f is:" +f);
		System.out.println("The value of char data type is:"+z);
		
		//output
				/*
				 The value of a is : 127
		The value of b is : 128
		The value of c is : 0.0
		The value of d is : 2.0
		The value of e is : 2.0
		The value of f is : 2.4
		The value of z is : s
		The value of x is : 99
		The value of name  is : Sahil
				 */
	}

}
